
class casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       byte a=10;
       int b=a;
       System.out.println(b);
       
       float x=20.2f;
       int y=(int)x;
       System.out.println(y);
      double m=25.88;
       float n=(float)m;
      System.out.println(n);
	}

}
